package springweb.z01_vo;

public class Gimbab {
	private int cnt;
	private int price;
	public Gimbab() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Gimbab(int cnt, int price) {
		super();
		this.cnt = cnt;
		this.price = price;
	}
	public int getCnt() {
		return cnt;
	}
	public void setCnt(int cnt) {
		this.cnt = cnt;
	}
	public int getPrice() {
		return price;
	}
	public void setPrice(int price) {
		this.price = price;
	}
	
}
